import React, { Component } from "react";
import HeaderMarket from './headerMarket';
import "./deposit.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container} from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Web3 from 'web3';
import dai from "../dai.svg";
import { render } from "react-dom";

export default class PersonalLoan extends Component{
    async componentDidMount(){
         this.displayString("0x8e34f42dDeDb350198087c0EedbcEEaAc546cDD7")
    }
    async displayString(str) {
        const prefix = str.substring(0, 6);
        const suffix = str.substring(str.length - 4, str.length);
        this.setState({accoutn: `prefix....suffix`}) ;
    }
    constructor(props){
        super(props);
        this.state= {
            account: 0
        }
    }
    render(){
        return(
            <>
                <div>
                    <HeaderMarket/>
                </div>
                <div className="deposti">
                    <Container className="border p-4">
                        <Row xs={1} md={3} className="g-4">
                            {Array.from({ length: 4 }).map((_, idx) => (
                                <Col>
                                <Card border="primary" className="mt-3">
                                    <Card.Body>
                                    <Card.Title>Owner: {this.state.account} </Card.Title>
                                    <Card.Text>
                                        This is a longer card with supporting text below as a natural
                                        lead-in to additional content. This content is a little bit
                                        longer.
                                    </Card.Text>
                                    </Card.Body>
                                </Card>
                                </Col>
                            ))}
                        </Row>
                    </Container>
                </div>
            </>
        )
    }
}
