import React, {Component, useState} from "react";
import { myAccount } from './account';
import {
    BrowserRouter as Router,
    Route,
    Link,
    Navigate 
}from "react-router-dom"
import { Routes  } from 'react-router-dom'
export default class LoginForm extends Component{
    constructor(props){
        super(props);
        this.state ={
            account : '',
            password: '',
            redirect: 0
        }
    }
    handleSubmit(event) {
    event.preventDefault();
    if((this.state.account == 'admin') && (this.state.password == '123456')){
        this.setState({  redirect: 1 });
    }else{
        this.setState({  redirect: 0 });
    }
    }
    setParam(event){
        this.setState({[event.target.name]: event.target.value})
    }
    render(){
    this.handleSubmit = this.handleSubmit.bind(this);
    this.setParam = this.setParam.bind(this);
    if (this.state.redirect == 1) {
      return <Navigate to="/admin" />
    }
    return(
        <form action="" onClick={this.handleSubmit}>
        <div className="mb-3 mt-3">
            <label  className="form-label">Tên tài khoản:</label>
            <input type="text" className="form-control" id="email" placeholder="Enter email" name="account" onChange={this.setParam}/>
        </div>
        <div className="mb-3">
            <label  className="form-label">Password:</label>
            <input type="password" className="form-control" id="pwd" placeholder="Enter password" name="password" onChange={this.setParam}/>
        </div>
        <div className="form-check mb-3">
            <label className="form-check-label">
            <input className="form-check-input" type="checkbox" name="remember"/> Remember me
            </label>
        </div>
        <button type="submit"  className="btn btn-primary">Submit</button>
        </form>
    )
    }
}
