import React, { Component } from "react";
import HeaderMarket from './headerMarket';
import "./dashboard.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container} from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import { FaWallet,FaSignal } from "react-icons/fa";
import Web3 from 'web3';
import iuhBank from '../abis/iuhBank.json';
import IuhCoin from '../abis/iuhCoin.json';
import Oracle from '../abis/Oracle.json';
import dai from "../dai.svg";

export default class Dashboard extends Component{
    async loadBlockchainData(dispatch) {
        if(typeof window.ethereum !== 'undefined'){
        const web3 = new Web3(window.ethereum)
        const netId = await web3.eth.net.getId()
        const accounts = await web3.eth.getAccounts()
        if(typeof accounts[0] !== 'undefined'){
            const balance = await web3.eth.getBalance(accounts[0])
            this.setState({account: accounts[0], balance: balance / 10 **18, web3: web3})
        } else {
            window.alert('Please login with MetaMask')
        }
        try {
            const iuhCoin = new web3.eth.Contract(IuhCoin.abi, IuhCoin.networks[netId].address)
            const IuhBank = new web3.eth.Contract(iuhBank.abi, iuhBank.networks[netId].address)
            const iuhBankAddress = iuhBank.networks[netId].address;
            const iuhCoinBalance = await iuhCoin.methods.balanceOf(accounts[0]).call();
            let userSupply = await IuhBank.methods.getUserSupplied().call({from: this.state.account});
            let userBorrow = await IuhBank.methods.getUserBorrow().call({from: this.state.account});
            let etherPool = await IuhBank.methods.getEtherPoolInfo().call({from: this.state.account});
            let iuhCoinPool = await IuhBank.methods.getIuhCoinPoolInfo().call({from: this.state.account});
            console.log(IuhCoin.networks[netId].address);
            this.setState({token: iuhCoin, iuhBank: IuhBank, iuhBankAddress: iuhBankAddress, userSupply: userSupply, userBorrow: userBorrow, etherPool: etherPool, iuhCoinPool: iuhCoinPool, iuhCoinBalance: iuhCoinBalance / 10 ** 18})
            
            this.state.connect = true;
        } catch (e) {
                console.log(e)
                window.alert('Contracts not deployed to the current network')
            }
        } else {
        window.alert('Please install MetaMask')
        }
    }
    async getInfoUser(){
        const userInfo = await this.state.iuhBank.methods.getUserSupplied().call({from: this.state.account});
        console.log(userInfo)
    }
    constructor(props){
        super(props);
        this.state ={
            web3: null,
            iuhBank: null, 
            token: null,
            iuhBankAddress: null,
            etherPool: null,
            iuhCoinPool: null,
            userSupply: [],
            userBorrow: [],
            account: null,
            balance: 0,
            iuhCoinBalance: 0,
            connect: false
        }
    }
    render(){
        return(
            <>
                {<HeaderMarket/>}
                    <div className="top  text-light">
                        <Container id="dashboardTop">
                            <h2>Iuh Market</h2>
                            <div className="d-flex">
                                <div className="d-flex">
                                    <div>
                                        <FaWallet className="h-100 w-100"/>
                                    </div>
                                    <div style={{width: "100px", marginLeft: "10px"}}>
                                        <p className="m-0 p-0">Net Worth</p>
                                        <h5>$ 0</h5>
                                    </div>
                                </div>
                                <div style={{paddingLeft: 30}} className="d-flex">
                                    <div>
                                        <FaSignal className="h-100 w-100"/>
                                    </div>
                                    <div style={{width: "100px", marginLeft: "10px"}}>
                                    <p className="m-0 p-0" >Net APY</p>
                                    <h5>$ -</h5>
                                    </div>
                                </div>
                            </div>  
                        </Container>
                    </div>
                    {this.state.connect == false && <div>
                            <Button variant="btn btn-primary" onClick={this.loadBlockchainData.bind(this)}>Connect</Button>
                    </div>}
                    {this.state.connect == true && <div>
                    <Container>
                        <div className="bottom">
                        <div className="bottomTop d-flex">
                            <div className="left">
                                <h5>Your Supplies</h5>
                                {this.state.userSupply.length != 0 && <div>
                                    <table>
                                        {this.state.userSupply['tokenName'].map((user) =>
                                            <div>
                                                user;
                                            </div>
                                        )}
                                    </table>
                                </div> }
                            </div>
                            <div className="right">
                                <h5>Your borrows</h5>
                                <p>Nothing borrowed yet</p>
                            </div>
                        </div>
                        <div className="bottomEnd d-flex">
                            <div className="left">
                                <h5>Asset to supply</h5>
                                <table className="w-100">
                                    <tr>
                                        <td className="text-center">Assets</td>
                                        <td className="text-center">Wallet balance</td>
                                        <td className="text-center">APY</td>
                                        <td className="text-center">Collateral</td>
                                        <td></td>
                                    </tr>
                                    <tr>
                                        <td className="text-center">
                                            <img src={dai} style={{width: "30px"}}/>
                                            <span className="h6 m-1">Ether</span>
                                        </td>
                                        <td className="text-center"> {this.state.balance} </td>
                                        <td className="text-center">{this.state.etherPool.lendRate}</td>
                                        <td className="text-center">Yes</td>
                                        <td className="text-center">
                                            <a href="/EtherDeposit"><Button variant="outline-primary">Supply</Button>{' '}</a>
                                        </td>
                                    </tr>
                                    <tr className="mt-3" >
                                        <td className="text-center">
                                            <img src={dai} style={{width: "30px"}}/>
                                            <span className="h6 m-1">IuhCoin</span>
                                        </td>
                                        <td className="text-center"> {this.state.iuhCoinBalance} </td>
                                        <td className="text-center">{this.state.iuhCoinPool.lendRate}</td>
                                        <td className="text-center">Yes</td>
                                        <td className="text-center">
                                            <a><Button variant="outline-primary">Supply</Button>{' '}</a>
                                        </td>
                                    </tr>
                                </table>
                            </div>
                            <div className="right">
                                <h5>Asset to borrow</h5>
                                <table className="w-100">
                                    <tr>
                                        <td className="text-center">Assets</td>
                                        <td className="text-center">Borrow</td>
                                        <td className="text-center">APY</td>
                                        <td className="text-center">Collateral</td>
                                        <td></td>
                                    </tr>
                                    <tr>
                                        <td className="text-center">
                                            <img src={dai} style={{width: "30px"}}/>
                                            <span className="h6 m-1">Ether</span>
                                        </td>
                                        <td className="text-center"> {this.state.etherPool.borrowAmount} </td>
                                        <td className="text-center">{this.state.etherPool.borrowRate}</td>
                                        <td className="text-center">Yes</td>
                                        <td className="text-center">
                                            <a><Button variant="outline-primary">Borrow</Button>{' '}</a>
                                        </td>
                                    </tr>
                                    <tr>
                                        <td className="text-center">
                                            <img src={dai} style={{width: "30px"}}/>
                                            <span className="h6 m-1">IuhCoin</span>
                                        </td>
                                        <td className="text-center">{this.state.iuhCoinPool.borrowAmount} </td>
                                        <td className="text-center">{this.state.iuhCoinPool.borrowRate}</td>
                                        <td className="text-center">Yes</td>
                                        <td className="text-center">
                                            <a>      <Button variant="outline-primary">Borrow</Button>{' '}</a>
                                        </td>
                                    </tr>
                                </table>
                            </div>
                        </div>
                    </div>
                    </Container>
                    </div>}
            </>
        )
    }
}
