import React, { Component } from "react";
import HeaderMarket from './headerMarket';
import "./deposit.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container} from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Web3 from 'web3';
import dai from "../dai.svg";
import { render } from "react-dom";

export default class PersonalLoan extends Component{
    async componentDidMount(){
         this.displayString("0x8e34f42dDeDb350198087c0EedbcEEaAc546cDD7")
    }
    async displayString(str) {
        const prefix = str.substring(0, 6);
        const suffix = str.substring(str.length - 4, str.length);
        this.setState({account: `${prefix}....${suffix}`}) ;
    }
    constructor(props){
        super(props);
        this.state= {
            account: 0,
            state: false
        }
    }
    render(){
        return(
            <>
                <div>
                    <HeaderMarket/>
                </div>
                <div className="deposti">
                    <Container className="border p-4">
                        {this.state.state == false && <div>
                            <h3>Personal loan area</h3>
                        <Button variant="btn btn-primary" onClick={(e) =>{
                            e.preventDefault();
                            this.setState({state: true})
                        }}>
                            Create new loan
                        </Button>
                        <Row xs={1} md={3} className="g-4">
                            {Array.from({ length: 4 }).map((_, idx) => (
                                <Col>
                                <Card border="primary" className="mt-3 ">
                                    <Card.Body>
                                    <Card.Title>Owner: {this.state.account} </Card.Title>
                                    <Card.Text className="w-100">
                                        Lend: 10 Ether <br/>
                                        Collateral: 100 IuhCoin<br/>
                                        Time: 3 day<br/>
                                    </Card.Text>
                                    </Card.Body>
                                    <Card.Footer>
                                         <a className="m-auto w-100 "><Button variant="btn w-100 btn-primary">
                                            Borrow    
                                        </Button></a>
                                    </Card.Footer>
                                </Card>
                                </Col>
                            ))}
                        </Row></div>}
                        {this.state.state == true && <div>
                            <Form>
                                <Row className="mb-3">
                                    <Form.Group as={Col} controlId="formGridEmail">
                                    <Form.Label>Token</Form.Label>
                                    <Form.Control type="email" placeholder="Enter token" />
                                    </Form.Group>

                                    <Form.Group as={Col} controlId="formGridPassword">
                                    <Form.Label>Amount</Form.Label>
                                    <Form.Control type="number" placeholder="Amount" />
                                    </Form.Group>
                                </Row>
                                <Row className="mb-3">
                                    <Form.Group as={Col} controlId="formGridEmail">
                                    <Form.Label>Token collater</Form.Label>
                                    <Form.Control type="email" placeholder="Enter token to collateral" />
                                    </Form.Group>

                                    <Form.Group as={Col} controlId="formGridPassword">
                                    <Form.Label>Amount</Form.Label>
                                    <Form.Control type="number" placeholder="Amount" />
                                    </Form.Group>
                                </Row>
                                <Row>
                                    <Form.Group as={Col}  controlId="formGridState">
                                    <Form.Label>State</Form.Label><br></br>
                                    <select className="w-100">
                                        <option>fdkajf</option>
                                    </select>
                                    </Form.Group>
                                </Row>
                                <Button variant="primary" type="submit">
                                    Submit
                                </Button>
                            </Form>
                        </div>}
                    </Container>
                </div>
            </>
        )
    }
}
