import React, { Component, useState } from "react";
import HeaderMarket from './headerMarket';
import "./deposit.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container,Form, InputGroup } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import dai from "../dai.svg";
import {Tabs, Nav} from 'react-bootstrap';
import Tab from 'react-bootstrap/Tab';
export default function getOtherCoin(){
const [activeTab, setActiveTab] = useState(1);

  const handleSelect = (key) => {
    setActiveTab(key);
  };

  const handleNextTab = () => {
    setActiveTab(activeTab  % 3 +1);
  };
        return(
            <>
                <div>
                    {<HeaderMarket />}
                </div>
                <div>
                        <div className="deposit">
                            <a href="/home">
                                <Button variant="outline-secondary">Back</Button>{' '}
                            </a>
                            <div className="text-center mt-5">
                                <h4 className="text-primary">
                                    Deposit Overview
                                </h4>
                                <p>
                                    There is your transaction details. Make sure to<br></br> check if this is correct before submitting
                                </p>
                                <form>
                                    <Form.Label htmlFor="disabledTextInput">Amount  </Form.Label>
                                </form>
                                <div className="mb-3 w-50 m-auto ">
                                    
                                    <Tabs activeKey={activeTab} onSelect={handleSelect} variant="pills" justify className = 'bg-light w-100 m-auto border'>
                                        <Tab eventKey={1} title="1 Approve" disabled>
                                            <Tab.Content className="row">
                                                <h6 className="p-3 col-9" style={{textAlign: "start"}}><span className="h5 text-primary">1/3 Approve</span> <br/>Please approve before deposit</h6>
                                                <Button onClick={handleNextTab} variant="btn btn-primary h-25 m-auto">Approve</Button>
                                            </Tab.Content>
                                        </Tab>
                                        <Tab eventKey={2} title="2 Deposit" disabled>
                                            <Tab.Content className="row">
                                                <h6 className="p-3 col-9" style={{textAlign: "start"}}><span className="h5 text-primary">1/2 Deposit</span> <br/>Pleasde submit to deposit</h6>
                                                <Button onClick={handleNextTab} variant="btn btn-primary h-25 m-auto">Deposit</Button>
                                            </Tab.Content>
                                        </Tab>
                                    
                                    <Tab eventKey={3} title="3 Pending" disabled>
                                        <Tab.Content className="row">
                                            <h6 className="p-3 col-9" style={{textAlign: "start"}}><span className="h5 text-primary">2/2 Finishes</span> <br/>Press button to back to home</h6>
                                            <Button onClick={handleNextTab} variant="btn btn-primary h-25 m-auto">Dashboard</Button>
                                        </Tab.Content>
                                    </Tab>
                                  </Tabs>
                                </div>
                            </div>
                        </div>
                </div>
            </>
        )
    
}
