  async lend(address,amount,interest) {
    if(this.state.dbank!=='undefined'){
      try{
        console.log(address)
        console.log(amount)
        console.log(interest)

        // Sau đó trả lại cho người dùng tiền đã thế chấp
        await this.state.dbank.methods.lendEther(address,amount,interest).send({value: amount.toString(), from: this.state.account});
