import { Tabs, Tab } from 'react-bootstrap'
import dBank from '../abis/dBank.json'
import React, { Component } from 'react';
import Token from '../abis/Token.json'
import dbank from '../dbank.png';
import Web3 from 'web3';
import './App.css';
import {userAccount} from './account';
import {
    BrowserRouter as Router,
    Navigate 
}from "react-router-dom"
let amount = JSON.parse(localStorage.getItem('amount')) || [0];  
export default class Begin extends Component{
  render (){
    if(userAccount === 1){
      return <User socket = {this.props.socket}/>
    }
    return <Navigate to="/" />
  }
}

class User extends Component{
  async componentWillMount() {
    await this.loadBlockchainData(this.props.dispatch)
  }
  async loadBlockchainData(dispatch) {
    if(typeof window.ethereum !== 'undefined'){
      const web3 = new Web3(window.ethereum)
      window.ethereum.enable();
      const netId = await web3.eth.net.getId()
      const accounts = await web3.eth.getAccounts()
      const account =[];
      
      if(typeof accounts[0] !== 'undefined'){
        const balance = await web3.eth.getBalance(accounts[0])
        this.setState({account: accounts[0], balance: balance, web3: web3})
      } else {
        window.alert('Please login with MetaMask')
      }
      try {
        const token = new web3.eth.Contract(Token.abi, Token.networks[netId].address)
        const dbank = new web3.eth.Contract(dBank.abi, dBank.networks[netId].address)
        const dBankAddress = dBank.networks[netId].address
        this.setState({token: token, dbank: dbank, dBankAddress: dBankAddress})
      } catch (e) {
        console.log('Error', e)
        window.alert('Contracts not deployed to the current network')
      }
    } else {
      window.alert('Please install MetaMask')
    }
  }
  async sendAddress(){
    this.props.socket.emit('join',this.state.account);
  }
  handleRequest = (e) => {
  e.preventDefault();
  let account = this.state.account;
      console.log(account);
  return fetch('http://localhost:3000/request', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({account: account}),
  })
    .then((res) => {
      if (!res.ok) {
        throw new Error('Network response was not ok');
      }
      return res.json();
    })
    .then((data) => {
      localStorage.setItem('amount',data);
      window.location.reload();
    })
    .catch((error) => {
      console.error('Error:', error);
    });
};
  async coc(amount) {
    if(this.state.dbank!=='undefined'){
      try{
      await this.state.dbank.methods.Coc().send({value: amount.toString(), from: this.state.account});
      localStorage.setItem('amount',0);
      window.location.reload();
      } catch (e) {
        alert('that bai');
             window.location.reload();

      }
    }
  }
  async withdraw(e) {
    e.preventDefault()
    if(this.state.dbank!=='undefined'){
      try{
        await this.state.dbank.methods.withdraw().send({from: this.state.account})
      } catch(e) {
        alert('that bai');
        console.log('Error, withdraw: ', e)
      }
    }
  }
  async CocWithDeposit(amount){
    if(this.state.dbank!=='undefined'){
      try{
        amount = amount * 10**18
        await this.state.dbank.methods.CocWithEtherDeposit(amount.toString()).send({from:this.state.account});
        console.log(amount);
        } catch (e) {
        // báo lỗi nếu việc lấy giá trị bị lỗi
        console.log('Error, deposit: ', e)
      }
    }
  }
  async CocWithToken(amount){
    if(this.state.dbank!=='undefined'){
      try{
        amount = amount * 10**18
        await this.state.dbank.methods.CocWithToken(amount.toString()).send({from:this.state.account});
        fetch('http://localhost:3000/request', {
          method: 'POST',
          headers: {
          'Content-Type': 'application/json',
            },
    body: JSON.stringify({account: account}),
  })
        
        } catch (e) {
        // báo lỗi nếu việc lấy giá trị bị lỗi
        console.log('Error, deposit: ', e)
      }
    }
  }
  async deposit(amount) {
    if(this.state.dbank!=='undefined'){
      try{
        // dùng để lấy ether từ account đang liên kết vào tiền gửi
        await this.state.dbank.methods.deposit().send({value: amount.toString(), from: this.state.account})
      } catch (e) {
        // báo lỗi nếu việc lấy giá trị bị lỗi
        console.log('Error, deposit: ', e)
      }
    }
  }
  async lend(address,amountSend,interest) {
    if(this.state.dbank!=='undefined'){
      try{
        console.log(address)
        console.log(amountSend)
        console.log(interest)
        amountSend *= 10**18
        // Sau đó trả lại cho người dùng tiền đã thế chấp
        await this.state.dbank.methods.lendEther(address,amountSend.toString(),interest).send({to: address,value: amountSend.toString(),from: this.state.account});
      } catch (e) {
        console.log('Error, deposit: ', e)
      }
    }
  }
  constructor(props) {
    super(props)
    this.state = {
      web3: 'undefined',
      account: '',
      token: null,
      dbank: null,
      balance: 0,
      dBankAddress: null,
    }
  }

  // dùng để tạo giao diện website 
  render() {
    return (

      <div className='text-monospace' >
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="#"
            target="_blank"
            rel="noopener noreferrer"
          >
        <img src={dbank} className="App-logo" alt="logo" height="32"/>
          <b>d₿ank</b>
          
        </a>
        </nav>
        <div className="container-fluid mt-5 text-center">
        <br></br>
          <h1>Welcome to d₿ank</h1>
          <h2>{this.state.account}</h2>
            <br></br>
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
              <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
                <Tab eventKey="send" title = "Send">
                  <div>
                    <form>
                    {(amount != 10) && <div>
                      <br></br>
                      <button type='button'  onClick={this.sendAddress.bind(this)}className='btn btn-primary'>Send Address</button><br></br>
                      <br></br>
                      <button type='submit'  onClick={this.handleRequest.bind(this)}className='btn btn-primary'>Refresh</button>
                    </div>
                    }
                    {(amount == 10) && 
                         <div>
                  <br></br>
                      <p>Do you want to deposit?</p>
                    <form onSubmit={(e) => {
                      e.preventDefault()
                      amount = amount * 10**18;
                      this.coc(amount);
                    }}>
                      {/* tạo form cho người dùng nhạp số lượng */}
                      <div className='form-group mr-sm-2'>
                      <br></br>
                        <input
                          type='number'
                          className="form-control form-control-md"
                          placeholder= {amount}
                          disabled />
                      </div>
                      <button type="submit" className='btn btn-primary'>DEPOSIT</button>{" "}
                      <button type="submit" className='btn btn-primary' onClick={(e) => {
                      e.preventDefault()
                      amount = amount;
                      this.CocWithDeposit(amount);}} >Deposit with deposit</button><br/>
                      <button type="submit" className='mt-2 btn btn-primary' onClick={(e) => {
                      e.preventDefault()
                      amount = amount;
                      this.CocWithToken(amount);}} >Deposit with Token</button>
                    </form>

                  </div>}
                    </form>
                    </div>
                </Tab>
                  {/* chức năng deposit */}
                <Tab eventKey="lend" title="lend">
                  <div>
                  <br></br>
                    How much do you want to deposit?
                    <br></br>
                    (min. amount is 0.01 ETH)
                    <br></br>
                    (1 deposit is possible at the time)
                    <br></br>
                    <form onSubmit={(e) => {
                      e.preventDefault()
                      let amountsend = this.amountSend.value
                      let interest = this.interest.value
                      let address = this.address.value
                      this.lend(address,amountsend,interest);
                    }}>
                      {/* tạo form cho người dùng nhạp số lượng */}
                      <div className='form-group mr-sm-2'>
                      <br></br>
                        <input
                          id='address'
                          type='text'
                          ref={(input) => { this.address = input }}
                          className="form-control form-control-md"
                          placeholder='adress...'
                          required />
                          <br></br>
                          <input
                          id='amountSend'
                          type='number'
                          ref={(input) => { this.amountSend = input }}
                          className="form-control form-control-md"
                          placeholder='amount to lend...'
                          required />
                          <br></br>
                          <input
                          id='interest'
                          type='number'
                          ref={(input) => { this.interest = input }}
                          className="form-control form-control-md"
                          placeholder='interest to lend...'
                          required />
                      </div>
                      <button type='submit' className='btn btn-primary'>lend</button>
                    </form>

                  </div>
                  </Tab>
                  <Tab eventKey="deposit" title="Deposit">
                  <div>
                  <br></br>
                    How much do you want to deposit?
                    <br></br>
                    (min. amount is 0.01 ETH)
                    <br></br>
                    (1 deposit is possible at the time)
                    <br></br>
                    {/* khi người dùng nhập vào số lượng và nhấn nút submit \ */}
                    <form onSubmit={(e) => {
                      e.preventDefault()
                      // lấy số lượng ether mà người dùng muốn gửi
                      let amount = this.depositAmount.value
                      // đổi ra giá trị wei 
                      amount = amount * 10**18 //convert to wei
                      // gọi hàm deposit với số lượng đó
                      this.deposit(amount)
                    }}>
                      {/* tạo form cho người dùng nhạp số lượng */}
                      <div className='form-group mr-sm-2'>
                      <br></br>
                        <input
                          id='depositAmount'
                          step="0.01"
                          type='number'
                          ref={(input) => { this.depositAmount = input }}
                          className="form-control form-control-md"
                          placeholder='amount...'
                          required />
                      </div>
                      <button type='submit' className='btn btn-primary'>DEPOSIT</button>
                    </form>

                  </div>
                  </Tab>
                  <Tab eventKey="withdraw" title="Withdraw">
                  <br></br>
                    Do you want to withdraw + take interest?
                    <br></br>
                    <br></br>
                  <div>
                    {/* tương tự như trên khi người dùng nhấn nút withdraw sẽ thực hiện hàm withdraw */}
                    <button type='submit' className='btn btn-primary' onClick={(e) => this.withdraw(e)}>WITHDRAW</button>
                  </div>
                </Tab>
                </Tabs>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}
