import React, { Component } from "react";
import HeaderMarket from './headerMarket';
import "./dashboard.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container} from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import { FaWallet,FaSignal } from "react-icons/fa";
import Web3 from 'web3';
import iuhBank from '../abis/iuhBank.json';
import IuhCoin from '../abis/iuhCoin.json';
import Oracle from '../abis/Oracle.json';
import dai from "../dai.svg";

export default class Dashboard extends Component{
    async loadBlockchainData(dispatch) {
        if(typeof window.ethereum !== 'undefined'){
        const web3 = new Web3(window.ethereum)
        const netId = await web3.eth.net.getId()
        const accounts = await web3.eth.getAccounts()
        const account =[];
        if(typeof accounts[0] !== 'undefined'){
            const balance = await web3.eth.getBalance(accounts[0])
            this.setState({account: accounts[0], balance: balance, web3: web3})
        } else {
            window.alert('Please login with MetaMask')
        }
        try {
            const iuhCoin = new web3.eth.Contract(IuhCoin.abi, IuhCoin.networks[netId].address)
            const IuhBank = new web3.eth.Contract(iuhBank.abi, iuhBank.networks[netId].address)
            const iuhBankAddress = iuhBank.networks[netId].address
            const userSupply = await IuhBank.methods.getUserSupplied().call({from: this.state.account});
            console.log(userSupply);
            this.setState({token: iuhCoin, iuhBank: IuhBank, iuhBankAddress: iuhBankAddress, userSupply: userSupply})
            
            this.state.connect = true;
        } catch (e) {
                console.log(e)
                window.alert('Contracts not deployed to the current network')
            }
        } else {
        window.alert('Please install MetaMask')
        }
    }
    async getInfoUser(){
        const userInfo = await this.state.iuhBank.methods.getUserSupplied().call({from: this.state.account});
        console.log(userInfo)
    }
    constructor(props){
        super(props);
        this.state ={
            web3: null,
            iuhBank: null, 
            token: null,
            iuhBankAddress: null,
            etherPool: [],
            iuhCoinPool: [],
            userSupply: [],
            userBorrow: [],
            account: null,
            amount: 10,
            connect: false
        }
    }
    render(){
        return(
            <>
                {<HeaderMarket/>}
                    <div className="top  text-light">
                        <Container id="dashboardTop">
                            <h2>Iuh Market</h2>
                            <div className="d-flex">
                                <div className="d-flex">
                                    <div>
                                        <FaWallet className="h-100 w-100"/>
                                    </div>
                                    <div style={{width: "100px", marginLeft: "10px"}}>
                                        <p className="m-0 p-0">Net Worth</p>
                                        <h5>$ 0</h5>
                                    </div>
                                </div>
                                <div style={{paddingLeft: 30}} className="d-flex">
                                    <div>
                                        <FaSignal className="h-100 w-100"/>
                                    </div>
                                    <div style={{width: "100px", marginLeft: "10px"}}>
                                    <p className="m-0 p-0" >Net APY</p>
                                    <h5>$ -</h5>
                                    </div>
                                </div>
                            </div>  
                        </Container>
                    </div>
                    {this.state.connect == false && <div>
                            <Button variant="btn btn-primary" onClick={this.loadBlockchainData.bind(this)}>Connect</Button>
