import { Component, useState } from 'react';
import React from 'react';
import { Tab, Tabs, TabList, TabPanel } from 'react';
 export default class DepositEther extends Component {
    handleNextTab = () => {
        this.setState=((this.state.activeTab + 1) % 3);
    };
  constructor(props) {
    super(props);
    this.state = {activeTab: 0};
  }
  render(){
  return (
    <Tabs selectedIndex={this.state.activeTab} onSelect={index => this.setState({activeTab: index})}>
      <TabList>
        <Tab>Tab 1</Tab>
        <Tab>Tab 2</Tab>
        <Tab>Tab 3</Tab>
      </TabList>

      <TabPanel>
        <h2>Content for Tab 1</h2>
        <button onClick={this.handleNextTab}>Next Tab</button>
      </TabPanel>
      <TabPanel>
        <h2>Content for Tab 2</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </TabPanel>
      <TabPanel>
        <h2>Content for Tab 3</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </TabPanel>
    </Tabs>
  );
}
}
