import React, {Component, useState} from "react";
import {
    BrowserRouter as Router,
    Route,
    Link,
    Navigate 
}from "react-router-dom";
import Token from '../abis/Token.json';
import dBank from '../abis/dBank.json';
import Web3 from 'web3';
export default class LoginForm extends Component{
    constructor(props){
        super(props);
        this.state ={
            account : '',
            password: '',
            admin: 0,
            user: 0,
        }
    }
    handleSubmit = (e) => {
  e.preventDefault();
  let account = this.state.account;
  let password = this.state.password;
  return fetch('http://localhost:3000/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ account, password }),
  })
    .then((res) => {
      if (!res.ok) {
        throw new Error('Network response was not ok');
      }
      return res.json();
    })
    .then((data) => {
      console.log(data)
      if(data == 1){
        this.setState({user: 1});
        console.log(data);
        localStorage.setItem('user',data);
      }
      if(data == '2'){
        this.setState({admin: 1});
        console.log(data);
        localStorage.setItem('admin',data);
      }
    })
    .catch((error) => {
      console.error('Error:', error);
    });
};
    setParam(event){
        this.setState({[event.target.name]: event.target.value})
    }
    render(){
    this.handleSubmit = this.handleSubmit.bind(this);
    this.setParam = this.setParam.bind(this);
    if (this.state.admin == 1) {
      return <Navigate to="/admin" />
    }
    if (this.state.user == 1) {
      return <Navigate to="/user" />
    }
    return(
        <form action="" onClick={this.handleSubmit}>
        <div className="mb-3 mt-3">
            {this.state.redirect}
            <label  className="form-label">Tên tài khoản:</label>
            <input type="text" className="form-control" id="email" placeholder="Enter email" name="account" onChange={this.setParam}/>
        </div>
        <div className="mb-3">
            <label  className="form-label">Password:</label>
            <input type="password" className="form-control" id="pwd" placeholder="Enter password" name="password" onChange={this.setParam}/>
        </div>
        <div className="form-check mb-3">
            <label className="form-check-label">
            <input className="form-check-input" type="checkbox" name="remember"/> Remember me
            </label>
        </div>
        <button type="submit"  className="btn btn-primary">Submit</button>
        </form>
    )
    }
}
class Connect extends Component{
   async loadBlockchainData(dispatch) {
  if(typeof window.ethereum !== 'undefined'){
      const web3 = new Web3(window.ethereum)
      await window.ethereum.enable();
      const netId = await web3.eth.net.getId()
      const accounts = await web3.eth.getAccounts()
      if(typeof accounts[0] !== 'undefined'){
        const balance = await web3.eth.getBalance(accounts[0])
        this.setState({account: accounts[0], balance: balance, web3: web3})
      } else {
        window.alert('Please login with MetaMask')
      }
        const token = new web3.eth.Contract(Token.abi, Token.networks[netId].address)
        const dbank = new web3.eth.Contract(dBank.abi, dBank.networks[netId].address)
        const dBankAddress = dBank.networks[netId].address}else {
      window.alert('Please install MetaMask')
    }}
constructor(props) {
    super(props)
    this.state = {
      web3: 'undefined',
      account: '',
      token: null,
      dbank: null,
      balance: 0,
      dBankAddress: null}}
    render(){

        return <Navigate to="/admin" />
    }
}
