import React, { Component, useState } from "react";
import HeaderMarket from './headerMarket';
import "./deposit.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container,Form, InputGroup } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import dai from "../dai.svg";
import {Tab, Tabs, Row} from 'react-bootstrap';

export default function GetEther(){
const [activeTab, setActiveTab] = useState(1);

  const handleSelect = (key) => {
    setActiveTab(key);
  };

  const handleNextTab = () => {
    setActiveTab(activeTab  % 3 +1);
  };
        return(
            <>
                <div>
                    {<HeaderMarket />}
                </div>
                <div>
                        <div className="deposit">
                            <a href="/home">
                                <Button variant="outline-secondary">Back</Button>{' '}
                            </a>
                            <div className="text-center mt-5">
                                <h4 className="text-primary">
                                    Deposit Overview
                                </h4>
                                <p>
                                    There is your transaction details. Make sure to<br></br> check if this is correct before submitting
                                </p>
                                <form>
                                    <Form.Label htmlFor="disabledTextInput">Amount  </Form.Label>
                                </form>
                                <div className="mb-3 w-50 m-auto">
                                <Row className="justify-content-center h6 ">
                                  <Tabs activeKey={activeTab} onSelect={handleSelect}>
                                    <Tab eventKey={1} title="Tab 1">
                                      <h2>Content for Tab 1</h2>
                                      <button onClick={handleNextTab}>Next Tab</button>
                                    </Tab>
                                    <Tab eventKey={2} title="Tab 2">
                                      <h2>Content for Tab 2</h2>
                                      <button onClick={handleNextTab}>Next Tab</button>
                                    </Tab>
                                    <Tab eventKey={3} title="Tab 3">
                                      <h2>Content for Tab 3</h2>
                                      <button onClick={handleNextTab}>Next Tab</button>
                                    </Tab>
                                </Tabs>
                              </Row>
                                </div>
                            </div>
                        </div>
                </div>
            </>
        )
    
}
