import { Tabs, Tab } from 'react-bootstrap'
import dBank from '../abis/dBank.json'
import React, { Component } from 'react';
import Token from '../abis/Token.json'
import dbank from '../dbank.png';
import Web3 from 'web3';
import {adminAccount} from './adminaccount';
import {
    BrowserRouter as Router,
    Navigate 
}from "react-router-dom"
let room = '';
let user = []
export default class Begin extends Component{
  render (){
    
    if(adminAccount === 2){
      return <Admin socket = {this.props.socket}/>
    }
    return <Navigate to="/" />
  }
}
class Admin extends Component{ 
  async componentWillMount() {
    await this.loadBlockchainData(this.props.dispatch)
  }
  async loadBlockchainData(dispatch) {
    if(typeof window.ethereum !== 'undefined'){
      const web3 = new Web3(window.ethereum)

      const netId = await web3.eth.net.getId()
      const accounts = await web3.eth.getAccounts()
          const account =[];
      
      if(typeof accounts[0] !== 'undefined'){
        const balance = await web3.eth.getBalance(accounts[0])
        this.setState({account: accounts[0], balance: balance, web3: web3})
      } else {
        window.alert('Please login with MetaMask')
      }
      try {
        const token = new web3.eth.Contract(Token.abi, Token.networks[netId].address)
        const dbank = new web3.eth.Contract(dBank.abi, dBank.networks[netId].address)
        const dBankAddress = dBank.networks[netId].address
        this.setState({token: token, dbank: dbank, dBankAddress: dBankAddress})

      } catch (e) {
        console.log('Error', e)
        window.alert('Contracts not deployed to the current network')
      }
    } else {
      window.alert('Please install MetaMask')
    }
  }
  
  async withstake(address) {
    if(this.state.dbank!=='undefined'){
      try{
        // dùng để lấy ether từ account đang liên kết vào tiền gửi
        await this.state.dbank.methods.withState(address).send({from: this.state.account})
      } catch (e) {
        // báo lỗi nếu việc lấy giá trị bị lỗi
        alert('ban khong co tien dat coc');
      }
    }
  }
  constructor(props) {
    super(props)
    this.state = {
      web3: 'undefined',
      account: '',
      token: null,
      dbank: null,
      balance: 0,
      dBankAddress: null
    }
  }
  async ReceiveAddress(){
    this.props.socket.emit('admin','enter');
    this.props.socket.on('room', (message) => {
    console.log(message);
    user = message;
    console.log(user)
  })}
  render() {
        this.ReceiveAddress = this.ReceiveAddress.bind(this);
    return (
      <div  className='text-monospace' onLoad={this.ReceiveAddress}>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="#"
            target="_blank"
            rel="noopener noreferrer"
          >
        <img src={dbank} className="App-logo" alt="logo" height="32"/>
          <b>d₿ank</b>
        </a>
        </nav>
        <div className="container-fluid mt-5 text-center">
        <br></br>
          <h1>Welcome to d₿ank</h1>
          <h2>{this.state.account}</h2>
            <br></br>
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
              <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
                <Tab eventKey='sendRequest' title ="Send Request">
                <form>
                    {(user != []) && <div>                      <div className='form-group mr-sm-2'>
                      <br></br>
                        <input

                          type='text'
                          className="form-control form-control-md"
                          placeholder= {user[1].account}
                          disabled />
                      <br></br>
                        <input
                          id='ether'
                          type='number'
                          ref={(input) => { this.ether = input }}
                          className="form-control form-control-md"
                          placeholder='stake...'
                          required />
                      </div>
                    <button type='button' className='btn btn-primary' onClick={(e) => {
                  let amount = this.ether.value
                      {this.props.socket.emit('message',{
                        value : amount,
                        account: user[1]['account']
                      })}
                    }}>Send</button></div>}
                    <br></br>
                    <button type='button' onClick={this.ReceiveAddress}  className='btn btn-primary' >Refresh</button>
                    </form>
                </Tab>
                {/* Chức năng withdraw */}
                <Tab eventKey="withStake" title="WithStake">
                     <form onSubmit={(e) => {
                      e.preventDefault()
                      let address = this.stake.value
                      {console.log(address)}
                      this.withstake(address)
                    }}>
                      <div className='form-group mr-sm-2'>
                      <br></br>
                        <input
                          id='stake'
                          type='text'
                          ref={(input) => { this.stake = input }}
                          className="form-control form-control-md"
                          placeholder='address...'
                          required />
                      </div>
                    <button type='submit' className='btn btn-primary'>withstake</button></form>
                </Tab>
                </Tabs>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}
