import { Tabs, Tab } from 'react-bootstrap'
import dBank from '../abis/dBank.json'
import React, { Component,useState } from 'react';
import Token from '../abis/Token.json'
import dbank from '../dbank.png';
import Web3 from 'web3';
import './App.css';
import {userAccount} from './account';
import {
    BrowserRouter as Router,
    Navigate 
}from "react-router-dom"
let amount = JSON.parse(localStorage.getItem('amount')) || [0]; 
export default class Begin extends Component{
  render (){
    if(userAccount === 1){
      return <User socket = {this.props.socket}/>
    }
    return <Navigate to="/" />
  }
}

class User extends Component{
  async loadBlockchainData(dispatch) {
    if(typeof window.ethereum !== 'undefined'){
      const web3 = new Web3(window.ethereum)
      await window.ethereum.enable();
      const netId = await web3.eth.net.getId()
      const accounts = await web3.eth.getAccounts()
      if(typeof accounts[0] !== 'undefined'){
        const balance = await web3.eth.getBalance(accounts[0])
        this.setState({account: accounts[0], balance: balance, web3: web3})
      } else {
        window.alert('Please login with MetaMask')
      }
        const token = new web3.eth.Contract(Token.abi, Token.networks[netId].address)
        const dbank = new web3.eth.Contract(dBank.abi, dBank.networks[netId].address)
        const dBankAddress = dBank.networks[netId].address
        this.setState({token: token, dbank: dbank, dBankAddress: dBankAddress})
        this.state.bankBanlace = await this.state.dbank.methods.getBalance(this.state.account).call({from:this.state.account});
        this.state.TokenBalance = await this.state.dbank.methods.getToken(this.state.account).call({from:this.state.account});
        this.state.stake = await this.state.dbank.methods.getStake(this.state.account).call({from:this.state.account});
        let lenderAmount = await this.state.dbank.methods.getLenderAmount().call({from: this.state.account});
        for (let index = 0; index < lenderAmount ; index++) {
          this.state.lender[index]={account:await this.state.dbank.methods.getLender(index).call({from:this.state.account})}
        }
        for (let index = 0; index < lenderAmount; index++) {
