import React, { Component } from "react";
import HeaderMarket from './headerMarket';
import "./deposit.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container,Form, InputGroup } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import dai from "../dai.svg";
import { Tab, Tabs, TabList, TabPanel } from 'react-bootstrap';
import { useState } from 'react';

export default class EtherDeposit extends Component{
    render(){
        return(
            <>
                <div>
                    {<HeaderMarket />}
                </div>
                <div>
                        <div className="deposit">
                            <a href="/home">
                                <Button variant="outline-secondary">Back</Button>{' '}
                            </a>
                            <div className="text-center mt-5">
                                <h4 className="text-primary">
                                    How much would you like to deposit?
                                </h4>
                                <p>
                                    Please enter an amount you would like to deposit. The maxium amount you <br></br> can deposit is shown below
                                </p>
                                <form>
                                    <Form.Label htmlFor="disabledTextInput">Avaliable to deposit </Form.Label>

                                    <InputGroup className="mb-3 w-25 m-auto">
                                        <Button variant="outline-secondary"><img src={dai}
                                        style={{width:"20px", backgroundColor: "White"}}
                                        /></Button>
                                        <Form.Control
                                        placeholder="Username"
                                        aria-label="Username"
                                        aria-describedby="basic-addon1"
                                        />
                                    </InputGroup>
                                    <Button variant=" mt-3 btn btn-primary">
                                        Deposit
                                    </Button>
                                </form>
                            </div>
                        </div>
                </div>
                {<DepositEther />}
            </>
        )
    }
}
 export function DepositEther() {
  const [activeTab, setActiveTab] = useState(0);

  const handleNextTab = () => {
    setActiveTab((activeTab + 1) % 3);
  };

  return (
    <Tabs selectedIndex={activeTab} onSelect={index => setActiveTab(index)}>
      <TabList>
        <Tab>Tab 1</Tab>
        <Tab>Tab 2</Tab>
        <Tab>Tab 3</Tab>
      </TabList>

      <TabPanel>
        <h2>Content for Tab 1</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </TabPanel>
      <TabPanel>
        <h2>Content for Tab 2</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </TabPanel>
      <TabPanel>
        <h2>Content for Tab 3</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </TabPanel>
    </Tabs>
  );
}
