import React, { Component } from "react";
import HeaderMarket from './headerMarket';
import "./deposit.css";
import 'bootstrap/dist/css/bootstrap.css';
import {Container,Form, InputGroup } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import dai from "../dai.svg";
import {Tab, Tabs, Row} from 'react-bootstrap';

class EtherDeposit extends Component{
    render(){
        return(
            <>
                <div>
                    {<HeaderMarket />}
                </div>
                <div>
                        <div className="deposit">
                            <a href="/home">
                                <Button variant="outline-secondary">Back</Button>{' '}
                            </a>
                            <div className="text-center mt-5">
                                <h4 className="text-primary">
                                    Deposit Overview
                                </h4>
                                <p>
                                    There is your transaction details. Make sure to<br></br> can deposit is shown below
                                </p>
                                <form>
                                    <Form.Label htmlFor="disabledTextInput">Avaliable to deposit </Form.Label>

                                    <InputGroup className="mb-3 w-25 m-auto">
                                        <Button variant="outline-secondary"><img src={dai}
                                        style={{width:"20px", backgroundColor: "White"}}
                                        /></Button>
                                        <Form.Control
                                        placeholder="Username"
                                        aria-label="Username"
                                        aria-describedby="basic-addon1"
                                        />
                                    </InputGroup>
                                    <Button variant=" mt-3 btn btn-primary">
                                        Deposit
                                    </Button>
                                </form>
                            </div>
                        </div>
                </div>
            </>
        )
    }
}

export default function GetEther() {
  const [activeTab, setActiveTab] = useState(1);

  const handleSelect = (key) => {
    setActiveTab(key);
  };

  const handleNextTab = () => {
    setActiveTab(activeTab  % 3 +1);
  };

  return (
    <Tabs activeKey={activeTab} onSelect={handleSelect}>
      <Tab eventKey={1} title="Tab 1">
        <h2>Content for Tab 1</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </Tab>
      <Tab eventKey={2} title="Tab 2">
        <h2>Content for Tab 2</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </Tab>
      <Tab eventKey={3} title="Tab 3">
        <h2>Content for Tab 3</h2>
        <button onClick={handleNextTab}>Next Tab</button>
      </Tab>
    </Tabs>
  );
}
