import { Tabs, Tab } from 'react-bootstrap'
import dBank from '../abis/dBank.json'
import React, { Component } from 'react';
import Token from '../abis/Token.json'
import dbank from '../dbank.png';
import Web3 from 'web3';
import './App.css';
import Connect from './connect';

export default class User extends Component{
  
  Connect

  async coc(amount) {
    if(this.state.dbank!=='undefined'){
      try{
        await this.state.dbank.methods.coc().send({value: amount.toString(), from: this.state.account})
      } catch (e) {
        console.log('Error, deposit: ', e)
      }
    }
  }
  async lend(address) {
    if(this.state.dbank!=='undefined'){
      try{
        const tokenLend = 10;
        // cấp quyền cho người dùng trả lại token đã mươn
        await this.state.token.methods.approve(this.state.dBankAddress, tokenLend.toString()).send({from: this.state.account})
        // Sau đó trả lại cho người dùng tiền đã thế chấp
        await this.state.dbank.methods.lend(address).send({from: this.state.account})
      } catch (e) {
        console.log('Error, deposit: ', e)
      }
    }
  }
  
  constructor(props) {
    super(props)
    this.state = {
      web3: 'undefined',
      account: '',
      token: null,
      dbank: null,
      balance: 0,
      dBankAddress: null
    }
  }
  // dùng để tạo giao diện website 
  render() {

    return (
      <div className='text-monospace'>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            href="#"
            target="_blank"
            rel="noopener noreferrer"
          >
        <img src={dbank} className="App-logo" alt="logo" height="32"/>
          <b>d₿ank</b>
        </a>
        </nav>
        <div className="container-fluid mt-5 text-center">
        <br></br>
          <h1>Welcome to d₿ank</h1>
          <h2>{this.state.account}</h2>
            <br></br>
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
              <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example">
                {/* chức năng deposit */}
                <Tab eventKey="deposit" title="Deposit">
                  <div>
                  <br></br>
                    How much do you want to deposit?
                    <br></br>
                    (min. amount is 0.01 ETH)
                    <br></br>
                    (1 deposit is possible at the time)
                    <br></br>
                    {/* khi người dùng nhập vào số lượng và nhấn nút submit \ */}
                    <form onSubmit={(e) => {
                      e.preventDefault()
                      // lấy số lượng ether mà người dùng muốn gửi
                      let amount = this.depositAmount.value
                      // đổi ra giá trị wei 
                      amount = amount * 10**18 //convert to wei
                      // gọi hàm deposit với số lượng đó
                      this.coc(amount);
                    }}>
                      {/* tạo form cho người dùng nhạp số lượng */}
                      <div className='form-group mr-sm-2'>
                      <br></br>
                        <input
                          id='depositAmount'
                          step="0.01"
                          type='number'
                          ref={(input) => { this.depositAmount = input }}
                          className="form-control form-control-md"
                          placeholder='amount...'
                          required />
                      </div>
                      <button type='submit' className='btn btn-primary'>DEPOSIT</button>
                    </form>

                  </div>
                  </Tab>
                  {/* chức năng deposit */}
                <Tab eventKey="lend" title="lend">
                  <div>
                  <br></br>
                    How much do you want to deposit?
                    <br></br>
                    (min. amount is 0.01 ETH)
                    <br></br>
                    (1 deposit is possible at the time)
                    <br></br>
                    <form onSubmit={(e) => {
                      e.preventDefault()

                      let address = this.address.value
                      this.lend(address);
                    }}>
                      {/* tạo form cho người dùng nhạp số lượng */}
                      <div className='form-group mr-sm-2'>
                      <br></br>
                        <input
                          id='address'
                          type='text'
                          ref={(input) => { this.address = input }}
                          className="form-control form-control-md"
                          placeholder='adress...'
                          required />
                      </div>
                      <button type='submit' className='btn btn-primary'>lend</button>
                    </form>

                  </div>
                  </Tab>
                </Tabs>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}
